####+BEGIN: b:py3:cs:cmnd/classHead :cmndName "platformBase" :comment "" :extent "verify" :ro "cli" :parsMand "" :parsOpt "bpoId" :argsMin 1 :argsMax 1 :pyInv ""
""" #+begin_org
*  _[[elisp:(blee:menu-sel:outline:popupMenu)][±]]_ _[[elisp:(blee:menu-sel:navigation:popupMenu)][Ξ]]_ [[elisp:(outline-show-branches+toggle)][|=]] [[elisp:(bx:orgm:indirectBufOther)][|>]] *[[elisp:(blee:ppmm:org-mode-toggle)][|N]]*  CmndSvc-   [[elisp:(outline-show-subtree+toggle)][||]] <<platformBase>>  =verify= parsOpt=bpoId argsMin=1 argsMax=1 ro=cli   [[elisp:(org-cycle)][| ]]
#+end_org """
class platformBase(cs.Cmnd):
    cmndParamsMandatory = [ ]
    cmndParamsOptional = [ 'bpoId', ]
    cmndArgsLen = {'Min': 1, 'Max': 1,}

    @cs.track(fnLoc=True, fnEntry=True, fnExit=True)
    def cmnd(self,
             rtInv: cs.RtInvoker,
             cmndOutcome: b.op.Outcome,
             bpoId: typing.Optional[str]=None,  # Cs Optional Param
             argsList: typing.Optional[list[str]]=None,  # CsArgs
    ) -> b.op.Outcome:

        failed = b_io.eh.badOutcome
        callParamsDict = {'bpoId': bpoId, }
        if self.invocationValidate(rtInv, cmndOutcome, callParamsDict, argsList).isProblematic():
            return failed(cmndOutcome)
        cmndArgsSpecDict = self.cmndArgsSpec()
        bpoId = csParam.mappedValue('bpoId', bpoId)
####+END:
        if self.cmndDocStr(f""" #+begin_org
** [[elisp:(org-cycle)][| *CmndDesc:* | ]]  With bpoId update or obtain /bisos/var/platform/bpo and /bisos/platform.
        #+end_org """): return(cmndOutcome)

        action = self.cmndArgsGet("0&1", cmndArgsSpecDict, argsList)[0]

        if action == "update":
            bpoBaseDir = bpo.bpoBaseDir_obtain(bpoId)
            bpoBasePath = Path(bpoBaseDir)

            bisosVarPlatform = Path("/bisos/var/platform")
            bisosVarPlatform.mkdir(parents=True, exist_ok=True)

            bisosVarPlatformBpo = bisosVarPlatform.joinpath("bpo")

            pathPlus.symlink_update(bpoBasePath, bisosVarPlatformBpo)
            # bisosVarPlatformBpo.symlink_to(bpoBasePath)

            pathPlus.symlink_update( bisosVarPlatformBpo, "/bisos/platform",)

            return cmndOutcome

        elif action == "obtain":
            return cmndOutcome.set(opResults=Path("/bisos/platform"))

        else:
            return failed(cmndOutcome)



####+BEGIN: b:py3:cs:method/args :methodName "cmndArgsSpec" :methodType "anyOrNone" :retType "bool" :deco "default" :argsList "self"
    """ #+begin_org
**  _[[elisp:(blee:menu-sel:outline:popupMenu)][±]]_ _[[elisp:(blee:menu-sel:navigation:popupMenu)][Ξ]]_ [[elisp:(outline-show-branches+toggle)][|=]] [[elisp:(bx:orgm:indirectBufOther)][|>]] *[[elisp:(blee:ppmm:org-mode-toggle)][|N]]*  Mtd-T-anyOrNone [[elisp:(outline-show-subtree+toggle)][||]] /cmndArgsSpec/ deco=default  deco=default  [[elisp:(org-cycle)][| ]]
    #+end_org """
    @cs.track(fnLoc=True, fnEntry=True, fnExit=True)
    def cmndArgsSpec(self, ):
####+END:
        """
***** Cmnd Args Specification
"""
        cmndArgsSpecDict = cs.CmndArgsSpecDict()
        cmndArgsSpecDict.argsDictAdd(
            argPosition="0&1",
            argName="action",
            argDefault='',
            argChoices=['update', 'obtain'],
            argDescription="One argument, as action"
        )
        return cmndArgsSpecDict



