__version__ = '0.0.8'
__usage__ = """
    USAGE:
        As a module from any location
            mypushover [-option value]

    OPTIONS:
        -c              config file path, if not set, 
                        use default path: ../site-packages/mypushover/pushover_conf.conf
        -start          start service in background, and enable startup script
        -stop           stop service
        -restart        restart service
        -disable        disable service and remove startup script
        -restart        restart service
        -status         show service status

"""
