PREFIX datacite: <http://purl.org/spar/datacite/>
PREFIX literal: <http://www.essepuntato.it/2010/06/literalreification/>
PREFIX fabio: <http://purl.org/spar/fabio/>

ASK {
    ?br1 datacite:hasIdentifier/literal:hasLiteralValue ?lit ;
    a fabio:Expression .
    ?br2 datacite:hasIdentifier/literal:hasLiteralValue ?lit ;
    a fabio:Expression .
    FILTER(?br1 != ?br2)
}

PREFIX datacite: <http://purl.org/spar/datacite/>
PREFIX literal: <http://www.essepuntato.it/2010/06/literalreification/>
PREFIX foaf: <http://xmlns.com/foaf/0.1/>

ASK {
    ?agent1 datacite:hasIdentifier/literal:hasLiteralValue ?lit ;
    a foaf:Agent .
    ?agent2 datacite:hasIdentifier/literal:hasLiteralValue ?lit ;
    a foaf:Agent .
    FILTER(?agent1 != ?agent2)
}

PREFIX frbr: <http://purl.org/vocab/frbr/core#>
PREFIX fabio: <http://purl.org/spar/fabio/>

ASK {
  ?br frbr:partOf ?venue;
    a fabio:Expression;
    frbr:embodiment ?man1, ?man2.
  FILTER(?man1 != ?man2)
  FILTER NOT EXISTS {
    ?br frbr:partOf ?otherVenue .
    FILTER(?venue != ?otherVenue)
  }
}

PREFIX frbr: <http://purl.org/vocab/frbr/core#>

ASK {
  ?br frbr:partOf ?venue1 ;
    frbr:partOf ?venue2 .
  # ?venue1 and ?venue2 have the same type (any type)
  ?venue1 a ?type .
  ?venue2 a ?type .
  FILTER(?venue1 != ?venue2)
}
